// app/(tabs)/dashboard.tsx
import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, FlatList, Button } from 'react-native';
import { startOfMonth, endOfMonth } from 'date-fns';
import { useTheme } from '@react-navigation/native';
import { supabase } from '../../utils/supabase'; // Assuming supabase is initialized here

interface Transaction {
  id: string;
  date: string;
  description: string;
  amount: number;
  type: 'income' | 'expense';
  main_category: string | null;
  subcategory: string | null;
  icon: string | null;
  installment_current?: number;
  installment_total?: number;
}

const Dashboard = () => {
  const [dateRange, setDateRange] = useState({
    startDate: startOfMonth(new Date()).toISOString().split('T')[0],
    endDate: endOfMonth(new Date()).toISOString().split('T')[0],
  });
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { colors } = useTheme();

  const workspace_id = 'your-workspace-id'; // Replace with your actual workspace ID

  useEffect(() => {
    loadData();
  }, [dateRange]);

  const loadData = async () => {
    setLoading(true);
    setError(null);

    try {
      const summary = await fetchMonthlySummary(workspace_id, dateRange.startDate, dateRange.endDate);
      const recentTransactions = await fetchRecentTransactions(workspace_id, dateRange.startDate, dateRange.endDate);

      if (summary) {
        const income = summary.find((item) => item.type === 'income')?.total || 0;
        const expenses = summary.find((item) => item.type === 'expense')?.total || 0;
        setTotalIncome(income);
        setTotalExpenses(expenses);
      }

      if (recentTransactions) {
        setTransactions(recentTransactions);
      }
    } catch (e: any) {
      setError(e.message || 'Failed to load data.');
    } finally {
      setLoading(false);
    }
  };

  const fetchMonthlySummary = async (workspace_id: string, startDate: string, endDate: string) => {
    const { data, error } = await supabase
      .from('transactions')
      .select('type, SUM(amount) as total')
      .eq('workspace_id', workspace_id)
      .gte('date', startDate)
      .lte('date', endDate)
      .groupBy('type');

    if (error) {
      console.error("Error fetching monthly summary:", error);
      throw error;
    }
    return data;
  };

  const fetchRecentTransactions = async (workspace_id: string, startDate: string, endDate: string) => {
    const { data, error } = await supabase
      .from('transactions')
      .select(`
        t.*,
        mc.name as main_category,
        mc.icon,
        sc.name as subcategory
      `)
      .eq('t.workspace_id', workspace_id)
      .gte('t.date', startDate)
      .lte('t.date', endDate)
      .order('t.date', { ascending: false })
      .order('t.created_at', { ascending: false });

    if (error) {
      console.error("Error fetching recent transactions:", error);
      throw error;
    }
    return data;
  };

  const handleDateChange = (newDateRange: { startDate: string; endDate: string }) => {
    setDateRange(newDateRange);
  };

  const formatCurrency = (amount: number) => {
    return Intl.NumberFormat('es-CL', { style: 'currency', currency: 'CLP', minimumFractionDigits: 0 }).format(amount);
  };

  const percentageSpent = (totalExpenses / totalIncome) * 100;
  let expenseCardColor = '#22C55E'; // green
  if (percentageSpent > 70 && percentageSpent <= 100) {
    expenseCardColor = '#F97316'; // orange
  } else if (percentageSpent > 100) {
    expenseCardColor = '#EF4444'; // red
  }

  return (
    <View style={[styles.container, { backgroundColor: colors.background }]}>
      <MonthSelector onDateChange={handleDateChange} />
      <View style={styles.cardsContainer}>
        <View style={styles.incomeCard}>
          <Text style={styles.cardText}>Total Income: {formatCurrency(totalIncome)}</Text>
        </View>
        <View style={[styles.expenseCard, { backgroundColor: expenseCardColor }]}>
          <Text style={styles.cardText}>Total Expenses: {formatCurrency(totalExpenses)}</Text>
          <Text style={styles.cardText}>Percentage Spent: {percentageSpent.toFixed(2)}%</Text>
        </View>
      </View>
      {loading && <Text>Loading...</Text>}
      {error && <Text style={{ color: 'red' }}>Error: {error}</Text>}
      {!loading && !error && (
        <TransactionsList transactions={transactions} formatCurrency={formatCurrency} />
      )}
    </View>
  );
};

const MonthSelector = ({ onDateChange }: { onDateChange: (dateRange: { startDate: string; endDate: string }) => void }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const monthYearFormat = new Intl.DateTimeFormat('es-CL', { month: 'long', year: 'numeric' });

  const handleMonthChange = (offset: number) => {
    const newDate = new Date(currentDate.getFullYear(), currentDate.getMonth() + offset, 1);
    setCurrentDate(newDate);
    const startDate = startOfMonth(newDate).toISOString().split('T')[0];
    const endDate = endOfMonth(newDate).toISOString().split('T')[0];
    onDateChange({ startDate, endDate });
  };

  return (
    <View style={styles.monthSelectorContainer}>
      <Button title="Previous" onPress={() => handleMonthChange(-1)} />
      <Text style={styles.monthText}>{monthYearFormat.format(currentDate)}</Text>
      <Button title="Next" onPress={() => handleMonthChange(1)} />
    </View>
  );
};

const TransactionsList = ({ transactions, formatCurrency }: { transactions: Transaction[]; formatCurrency: (amount: number) => string }) => {
  const renderItem = ({ item }: { item: Transaction }) => {
    const transactionDate = new Date(item.date);
    const formattedDate = `${transactionDate.getDate()} ${new Intl.DateTimeFormat('es-CL', { month: 'short' }).format(transactionDate)}`;
    const amountColor = item.type === 'income' ? '#22C55E' : '#EF4444';

    return (
      <View style={styles.transactionItem}>
        <Text>{formattedDate}</Text>
        <Text style={{ color: amountColor }}>{formatCurrency(item.amount)}</Text>
        <Text>{item.description}</Text>
        <Text>{item.main_category} > {item.subcategory}</Text>
      </View>
    );
  };

  return (
    <FlatList
      data={transactions}
      renderItem={renderItem}
      keyExtractor={(item) => item.id}
    />
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
  },
  monthSelectorContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  monthText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  cardsContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 10,
  },
  incomeCard: {
    backgroundColor: '#1a1a1a',
    padding: 15,
    borderRadius: 5,
    flex: 1,
    marginRight: 5,
  },
  expenseCard: {
    padding: 15,
    borderRadius: 5,
    flex: 1,
    marginLeft: 5,
  },
  cardText: {
    color: 'white',
  },
  transactionItem: {
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
});

export default Dashboard;
```

