// Función para calcular resumen financiero mensual
async function getMonthlyFinancialSummary(
  supabase: SupabaseClient,
  workspaceId: string,
  year: number,
  month: number // 0-11 (JavaScript Date format)
): Promise<{ totalIngresos: number; totalGastos: number; balance: number }> {

  // 1. Calcular fechas del mes en formato YYYY-MM-DD
  const monthDate = new Date(year, month, 1);
  const startDate = format(startOfMonth(monthDate), 'yyyy-MM-dd');
  const endDate = format(endOfMonth(monthDate), 'yyyy-MM-dd');

  console.log('📊 Fetching transactions:', { workspaceId, startDate, endDate });

  // 2. Query a Supabase - EXACTAMENTE como la app web
  const { data: transactions, error } = await supabase
    .from('transactions')
    .select('amount, type')  // Solo necesitamos amount y type
    .eq('workspace_id', workspaceId)
    .gte('date', startDate)  // >= inicio del mes
    .lte('date', endDate);   // <= fin del mes

  if (error) {
    console.error('Error fetching transactions:', error);
    throw error;
  }

  if (!transactions || transactions.length === 0) {
    return { totalIngresos: 0, totalGastos: 0, balance: 0 };
  }

  // 3. Calcular totales - EXACTAMENTE como la app web
  const totalIngresos = transactions
    .filter(t => t.type === 'income')
    .reduce((sum, t) => sum + Number(t.amount), 0);

  const totalGastos = transactions
    .filter(t => t.type === 'expense')
    .reduce((sum, t) => sum + Number(t.amount), 0);

  console.log('📊 Summary result:', {
    totalIngresos,
    totalGastos,
    balance: totalIngresos - totalGastos,
    transactionCount: transactions.length
  });

  return {
    totalIngresos,
    totalGastos,
    balance: totalIngresos - totalGastos
  };
}


