async function signUp(data: SignUpData) {
  const { email, password, nombre, numero_celular } = data;

  // 1. Validar campos obligatorios
  if (!email || !isValidEmail(email)) {
    return { error: 'Email inválido' };
  }
  if (!password || password.length < 6) {
    return { error: 'La contraseña debe tener al menos 6 caracteres' };
  }
  if (!nombre || nombre.trim().length < 2 || nombre.length > 100) {
    return { error: 'El nombre debe tener entre 2 y 100 caracteres' };
  }

  // 2. Verificar si el email ya existe
  const { data: emailExists } = await supabase
    .rpc('check_email_exists', { email_to_check: email.toLowerCase() });

  if (emailExists) {
    return { error: 'Este email ya está registrado' };
  }

  // 3. Validar teléfono (si se proporciona)
  let formattedPhone = '';
  if (numero_celular && numero_celular.trim() !== '') {
    formattedPhone = formatPhoneForStorage(numero_celular);

    if (!isValidChileanPhone(numero_celular)) {
      return { error: 'Número de celular inválido. Debe ser chileno de 9 dígitos' };
    }

    const { data: phoneExists } = await supabase
      .rpc('check_phone_exists', { phone_to_check: formattedPhone });

    if (phoneExists) {
      return { error: 'Este número ya está registrado' };
    }
  }

  // 4. Ejecutar signUp en Supabase Auth
