// lib/transactions.js

import { supabase } from './supabase';

/**
 * Crear una transacción simple (sin cuotas)
 */
export const createTransaction = async ({
  userId,
  type,           // 'expense' o 'income'
  amount,
  description,
  date,           // 'YYYY-MM-DD'
  subcategoryId,  // puede ser null
  paymentMethodType, // 'debit', 'credit', 'cash'
  workspaceId
}) => {
  console.log('[createTransaction] Creating:', { type, amount, description });

  const { data, error } = await supabase.rpc('insert_transaction_with_subcategory', {
    p_user_id: userId,
    p_type: type,
    p_amount: amount,
    p_description: description,
    p_date: date,
    p_subcategory_id: subcategoryId || null,
    p_payment_method_type: paymentMethodType || 'debit',
    p_source: 'mobile_app',
    p_workspace_id: workspaceId
  });

  if (error) {
    console.error('[createTransaction] Error:', error);
    throw error;
  }

  console.log('[createTransaction] Success');
  return data;
};

/**
 * Crear una transacción con cuotas (tarjeta de crédito)
 */
export const createTransactionWithInstallments = async ({
  userId,
  totalAmount,
  description,
  date,
  subcategoryId,
  installments,
  workspaceId
}) => {
  console.log('[createTransactionWithInstallments] Creating:', { 
    totalAmount, 
    installments,
    perInstallment: totalAmount / installments 
  });

  const { data, error } = await supabase.rpc('insert_transaction_with_installments', {
    p_user_id: userId,
    p_type: 'expense',
    p_total_amount: totalAmount,
    p_description: description,
    p_date: date,
    p_subcategory_id: subcategoryId || null,
    p_payment_method_type: 'credit',
    p_installments: installments,
    p_source: 'mobile_app',
    p_workspace_id: workspaceId
  });

  if (error) {
    console.error('[createTransactionWithInstallments] Error:', error);
    throw error;
  }

  console.log('[createTransactionWithInstallments] Success - Created', installments, 'installments');
  return data;
};

/**
 * Función helper que decide cuál usar
 */
export const saveTransaction = async (transactionData) => {
  const {
    userId,
    type,
    amount,
    description,
    date,
    subcategoryId,
    paymentMethodType,
    installments,
    workspaceId
  } = transactionData;

  // Si es crédito con más de 1 cuota, usar función de cuotas
  if (paymentMethodType === 'credit' && installments > 1) {
    return createTransactionWithInstallments({
      userId,
      totalAmount: amount,
      description,
      date,
      subcategoryId,
      installments,
      workspaceId
    });
  }

  // Sino, usar función simple
  return createTransaction({
    userId,
    type,
    amount,
    description,
    date,
    subcategoryId,
    paymentMethodType,
    workspaceId
  });
};

