 RETURNS jsonb
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  parsed_date date;
  installment_amount numeric;
  remaining_amount numeric;
  last_installment_amount numeric;
  installment_date date;
  main_category_id_value uuid;
  parent_id uuid;
  transaction_ids uuid[] := '{}';
  i integer;
  created_id uuid;
BEGIN
  -- Validar que las cuotas solo se usen con tarjeta de crédito
  IF p_installments > 1 AND p_payment_method_type != 'credit' THEN
    RAISE EXCEPTION 'Las cuotas solo están disponibles para pagos con tarjeta de crédito';
  END IF;

  -- Validar número de cuotas razonable
  IF p_installments < 1 OR p_installments > 48 THEN
    RAISE EXCEPTION 'El número de cuotas debe estar entre 1 y 48';
  END IF;

  -- Parsear fecha
  BEGIN
    IF p_date ~ '^\d{4}-\d{2}-\d{2}$' THEN
      parsed_date := p_date::date;
    ELSE
      parsed_date := DATE((p_date::timestamp) AT TIME ZONE 'America/Santiago');
    END IF;
  EXCEPTION WHEN OTHERS THEN
    parsed_date := DATE(now() AT TIME ZONE 'America/Santiago');
  END;

  -- Obtener main_category_id desde subcategoría
  IF p_subcategory_id IS NOT NULL THEN
    SELECT main_category_id INTO main_category_id_value
    FROM subcategories
    WHERE id = p_subcategory_id;
  END IF;

  -- Calcular monto por cuota (redondear hacia abajo para evitar exceder el total)
  installment_amount := FLOOR((p_total_amount / p_installments) * 100) / 100;

  -- Calcular el monto de la última cuota para compensar el redondeo
  remaining_amount := p_total_amount - (installment_amount * (p_installments - 1));
  last_installment_amount := remaining_amount;

  -- Crear las transacciones para cada cuota
  FOR i IN 1..p_installments LOOP
    -- Calcular fecha para esta cuota (sumar i-1 meses)
    installment_date := parsed_date + ((i - 1) || ' months')::interval;

    -- Usar el monto ajustado para la última cuota
    IF i = p_installments THEN
      installment_amount := last_installment_amount;
    END IF;

    -- Insertar la transacción
    INSERT INTO public.transactions (
      user_id,
      type,
      amount,
      description,
      date,
      subcategory_id,
      main_category_id,
      payment_method_type,
      affects_balance,
      source,
      installments,
      installment_number,
      parent_transaction_id,
      total_amount
    ) VALUES (
      p_user_id,
      p_type,
      installment_amount,
      p_description || ' - Cuota ' || i || '/' || p_installments,
      installment_date,
      p_subcategory_id,
      main_category_id_value,
      p_payment_method_type,
      false,
      p_source,
      p_installments,
      i,
      CASE WHEN i = 1 THEN NULL ELSE parent_id END,
      p_total_amount
    ) RETURNING id INTO created_id;

    -- Guardar el ID de la primera transacción como parent
    IF i = 1 THEN
      parent_id := created_id;
    END IF;

    -- Agregar ID al array de respuesta
    transaction_ids := array_append(transaction_ids, created_id);
  END LOOP;

  RAISE LOG 'Created % installment transactions for user %', p_installments, p_user_id;

  -- Retornar información de las transacciones creadas
  RETURN jsonb_build_object(
    'success', true,
    'installments_created', p_installments,
    'parent_transaction_id', parent_id,
    'transaction_ids', transaction_ids
  );
END;
