```typescript
import React, { useState, useEffect, useMemo } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from 'react-native';
import { useTheme } from '@react-navigation/native';
import { startOfMonth, endOfMonth } from 'date-fns';
import { supabase } from '../utils/supabase'; // Assuming you have supabase configured
import { Icon } from '@rneui/themed'; // or any other icon library
import { format } from 'date-fns';
import { es } from 'date-fns/locale';

interface Transaction {
  id: string;
  date: string;
  description: string;
  amount: number;
  type: 'income' | 'expense';
  main_category: string | null;
  subcategory: string | null;
  icon: string | null;
  installment_current?: number;
  installment_total?: number;
}

const Dashboard = () => {
  const theme = useTheme();
  const [date, setDate] = useState(new Date());
  const [startDate, setStartDate] = useState(startOfMonth(date).toISOString().split('T')[0]);
  const [endDate, setEndDate] = useState(endOfMonth(date).toISOString().split('T')[0]);
  const [summary, setSummary] = useState({ income: 0, expense: 0 });
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [page, setPage] = useState(1);
  const [typeFilter, setTypeFilter] = useState<'income' | 'expense' | 'all'>('all');
  const pageSize = 10;

  const workspace_id = 'your_workspace_id'; // Replace with your actual workspace ID

  useEffect(() => {
    setStartDate(startOfMonth(date).toISOString().split('T')[0]);
    setEndDate(endOfMonth(date).toISOString().split('T')[0]);
  }, [date]);

  useEffect(() => {
    loadData();
  }, [startDate, endDate, page, typeFilter]);

  const loadData = async () => {
    setLoading(true);
    setError(null);

    try {
      const { data: summaryData, error: summaryError } = await supabase
        .from('transactions')
        .select('type, sum(amount)')
        .eq('workspace_id', workspace_id)
        .gte('date', startDate)
        .lte('date', endDate)
        .groupBy('type');

      if (summaryError) {
        throw summaryError;
      }

      let income = 0;
      let expense = 0;

      summaryData.forEach((item: any) => {
        if (item.type === 'income') {
          income = item.sum || 0;
        } else if (item.type === 'expense') {
          expense = item.sum || 0;
        }
      });

      setSummary({ income, expense });

      let query = supabase
        .from('transactions')
        .select('*, main_categories(name, icon), subcategories(name)')
        .eq('workspace_id', workspace_id)
        .gte('date', startDate)
        .lte('date', endDate)
        .order('date', { ascending: false })
        .order('created_at', { ascending: false })
        .range((page - 1) * pageSize, page * pageSize - 1);

      if (typeFilter !== 'all') {
        query = query.eq('type', typeFilter);
      }

      const { data: transactionsData, error: transactionsError } = await query;

      if (transactionsError) {
        throw transactionsError;
      }

      setTransactions(transactionsData as Transaction[]);

    } catch (e: any) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  const goToPreviousMonth = () => {
    setDate(new Date(date.getFullYear(), date.getMonth() - 1, date.getDate()));
    setPage(1);
  };

  const goToNextMonth = () => {
    setDate(new Date(date.getFullYear(), date.getMonth() + 1, date.getDate()));
    setPage(1);
  };

  const handleIncomePress = () => {
    setTypeFilter('income');
    setPage(1);
  };

  const handleExpensePress = () => {
    setTypeFilter('expense');
    setPage(1);
  };

  const handleAllPress = () => {
    setTypeFilter('all');
    setPage(1);
  };

  const renderItem = ({ item }: { item: Transaction }) => (
    <View style={styles.transactionItem}>
      <View style={styles.transactionInfo}>
        <Text style={styles.transactionDescription}>{item.description}</Text>
        <Text style={styles.transactionCategory}>
          {item.main_category?.name} {item.subcategory?.name ? `> ${item.subcategory.name}` : ''}
        </Text>
      </View>
      <Text style={[styles.transactionAmount, item.type === 'income' ? styles.income : styles.expense]}>
        {item.type === 'income' ? '+' : '-'} {item.amount}
      </Text>
    </View>
  );

  const renderEmpty = () => (
    <View style={styles.emptyContainer}>
      <Text>No hay transacciones este mes</Text>
    </View>
  );

  const renderError = () => (
    <View style={styles.errorContainer}>
      <Text>Error al cargar las transacciones: {error}</Text>
      <TouchableOpacity onPress={loadData}>
        <Text>Reintentar</Text>
      </TouchableOpacity>
    </View>
  );

  const renderFooter = () => (
    <View style={styles.paginationContainer}>
      <TouchableOpacity onPress={() => setPage(page - 1)} disabled={page === 1}>
        <Text>Anterior</Text>
      </TouchableOpacity>
      <Text>{page}</Text>
      <TouchableOpacity onPress={() => setPage(page + 1)} disabled={transactions.length < pageSize}>
        <Text>Siguiente</Text>
      </TouchableOpacity>
    </View>
  );

  const percentageSpent = summary.income > 0 ? (summary.expense / summary.income) * 100 : 0;
  let expenseCardColor = theme.colors.success;

  if (percentageSpent > 70 && percentageSpent <= 100) {
    expenseCardColor = '#F97316'; // Orange
  } else if (percentageSpent > 100) {
    expenseCardColor = '#EF4444'; // Red
  }

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <View style={styles.monthSelector}>
        <TouchableOpacity onPress={goToPreviousMonth}>
          <Icon name="chevron-left" type="material" color={theme.colors.text} />
        </TouchableOpacity>
        <Text style={styles.monthText}>
          {format(date, 'MMMM yyyy', { locale: es }).toUpperCase()}
        </Text>
        <TouchableOpacity onPress={goToNextMonth}>
          <Icon name="chevron-right" type="material" color={theme.colors.text} />
        </TouchableOpacity>
      </View>

      <View style={styles.summaryContainer}>
        <TouchableOpacity style={[styles.incomeCard, { backgroundColor: theme.colors.success }]} onPress={handleIncomePress}>
          <Text style={styles.cardTitle}>Ingresos</Text>
          <Text style={styles.cardAmount}>${summary.income}</Text>
        </TouchableOpacity>

        <TouchableOpacity style={[styles.expenseCard, { backgroundColor: expenseCardColor }]} onPress={handleExpensePress}>
          <Text style={styles.cardTitle}>Gastos</Text>
          <Text style={styles.cardAmount}>${summary.expense}</Text>
          <Text>{percentageSpent.toFixed(1)}% de los ingresos</Text>
        </TouchableOpacity>
      </View>

      <Text style={styles.transactionsTitle}>Transacciones</Text>

      {loading ? (
        <Text>Cargando...</Text>
      ) : error ? (
        renderError()
      ) : transactions.length > 0 ? (
        <FlatList
          data={transactions}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
          ListFooterComponent={renderFooter}
          ListEmptyComponent={renderEmpty}
        />
      ) : (
        renderEmpty()
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  monthSelector: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 16,
  },
  monthText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  summaryContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 16,
  },
  incomeCard: {
    flex: 1,
    padding: 16,
    borderRadius: 8,
    marginRight: 8,
  },
  expenseCard: {
    flex: 1,
    padding: 16,
    borderRadius: 8,
    marginLeft: 8,
  },
  cardTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'white',
  },
  cardAmount: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
  },
  transactionsTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  transactionItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 8,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  transactionInfo: {
    flex: 1,
  },
  transactionDescription: {
    fontSize: 16,
  },
  transactionCategory: {
    fontSize: 12,
    color: '#666',
  },
  transactionAmount: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  income: {
    color: '#22C55E',
  },
  expense: {
    color: '#EF4444',
  },
  paginationContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingVertical: 16,
  },
  emptyContainer: {
    alignItems: 'center',
    paddingVertical: 16,
  },
  errorContainer: {
    alignItems: 'center',
    paddingVertical: 16,
  },
});

export default Dashboard;
```

